{
  "name": "Account",
  "description": "Account Provisioning Policy",
  "usageType": "CREATE",
  "fields": [
    {
      "name": "displayName",
      "transform": {
        "type": "identityAttribute",
        "attributes": {
          "name": "displayName"
        }
      },
      "attributes": {},
      "isRequired": false,
      "type": "string",
      "isMultiValued": false
    },
    {
      "name": "distinguishedName",
      "transform": {
        "type": "usernameGenerator",
        "attributes": {
          "sourceCheck": true,
          "patterns": [
            "CN=$fi $ln,OU=zzUsers,OU=Demo,DC=seri,DC=sailpointdemo,DC=com",
            "CN=$fti $ln,OU=zzUsers,OU=Demo,DC=seri,DC=sailpointdemo,DC=com",
            "CN=$fn $ln,OU=zzUsers,OU=Demo,DC=seri,DC=sailpointdemo,DC=com",
            "CN=$fn$ln${uniqueCounter},OU=zzUsers,OU=Demo,DC=seri,DC=sailpointdemo,DC=com"
          ],
          "fn": {
            "type": "identityAttribute",
            "attributes": {
              "name": "firstname"
            }
          },
          "ln": {
            "type": "identityAttribute",
            "attributes": {
              "name": "lastname"
            }
          },
          "fi": {
            "type": "substring",
            "attributes": {
              "input": {
                "type": "identityAttribute",
                "attributes": {
                  "name": "firstname"
                }
              },
              "begin": 0,
              "end": 1
            }
          },
          "fti": {
            "type": "substring",
            "attributes": {
              "input": {
                "type": "identityAttribute",
                "attributes": {
                  "name": "firstname"
                }
              },
              "begin": 0,
              "end": 2
            }
          }
        }
      },
      "attributes": {
        "cloudMaxUniqueChecks": "5",
        "cloudMaxSize": "100",
        "cloudRequired": "true"
      },
      "isRequired": false,
      "type": "",
      "isMultiValued": false
    },
    {
      "name": "description",
      "transform": {
        "type": "static",
        "attributes": {
          "value": ""
        }
      },
      "attributes": {},
      "isRequired": false,
      "type": "string",
      "isMultiValued": false
    }
  ]
}

